import React, { useState } from 'react';
import './ListeDeroulante.css'

import { useEffect } from 'react';

import config from '../../config';


import { accountService } from '../../services/account.service';
const apiKey = config.apiKey;


function DropdownMenu() {
    const [isAdmin, setIsAdmin] = useState(false);
    useEffect(() => {
        let user_id = accountService.getId();
        console.log("User ID:", user_id);
        fetch(`https://api.cogform.fr/users/${user_id}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'x-api-key': apiKey, // Assurez-vous que apiKey est défini correctement
            },
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Erreur lors de la récupération des informations de l\'utilisateur');
                }
                return response.json();
            })
            .then(data => {
                // Récupération des informations de l'utilisateur, y compris son statut d'administration
                if (data) {
                    // Si la réponse est OK, on enregistre le token dans le localStorage
                    console.log('Data ok');
                    console.log(data);
                    accountService.saveToken(data.token);
                    console.log('Token saved:', accountService.getToken());
                }
                else {
                    console.error('Erreur');
                }
            })
            .catch(error => {
                console.error('Erreur :', error);
                // Gérer l'erreur de récupération des informations de l'utilisateur
            });

        let token = accountService.getToken();
        fetch(`https://api.cogform.fr/is_admin/${token}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'x-api-key': apiKey,
            },
        })
            .then(response => response.json())
            .then(data => {
                if (data) {
                    // Si la réponse est OK, on enregistre le token dans le localStorage
                    console.log('Data ok');
                    console.log(data);
                    setIsAdmin(data);
                    accountService.saveAdmin(data);
                }
                else {
                    console.log('Data incorrect.');
                }
            })
            .catch(error => {
                console.error('Erreur lors de la récuperation des informations :', error);
            });
    }, []);

    return (
        <div className="dropdown">
            <button className="mainmenubtn">Compte</button>
            <div className="dropdown-child">
                {!accountService.isLogged() && (
                    <a href="/Compte">Me connecter</a>
                )}
                {!isAdmin && accountService.isLogged() && (
                    <div>
                        <a href="/MesInfos">Mes informations personnelles</a>
                        <a href="/MonParcours">Mon parcours</a>
                        <a href="/Deconnexion">Deconnexion</a>
                    </div>
                )}
                {isAdmin && accountService.isLogged() && (
                    <div>
                        <a href="/MesInfos">Mes informations personnelles</a>
                        <a href="/Gestion"> Gestion des attractions </a>
                        <a href="/Utilisateurs"> Gestion des utilisateurs</a>
                        <a href="/MyPage"> Gestion du parc</a>
                        <a href="/Deconnexion">Déconnexion</a>
                    </div>
                )}
            </div>
        </div>
    );
}

export default DropdownMenu;
